SELECT tblxTwo.X, tblxTwo.Y FROM (SELECT SUM(tblxOne.MinOfX) AS X, SUM(tblxOne.MaxOfY) AS Y, tblxOne.XY FROM (SELECT MIN(Functions.X) AS MinOfX, 0 AS MaxOfY, IIF([Functions].[X]<[Functions].[Y],CONCAT([Functions].[X],[Functions].[Y]),CONCAT([Functions].[Y],[Functions].[X])) AS XY FROM Functions INNER JOIN Functions AS Functions_1 ON (Functions.X = Functions_1.Y) AND (Functions_1.X = Functions.Y) GROUP BY IIF([Functions].[X]<[Functions].[Y],CONCAT([Functions].[X],[Functions].[Y]),CONCAT([Functions].[Y], [Functions].[X]))
UNION 
SELECT 0 AS MinOfX, MAX(Functions.X) AS MaxOfY, IIF([Functions].[X]<[Functions].[Y],CONCAT([Functions].[X],[Functions].[Y]),CONCAT([Functions].[Y],[Functions].[X])) AS XY FROM Functions INNER JOIN Functions AS Functions_1 ON (Functions.X = Functions_1.Y) AND (Functions_1.X = Functions.Y) GROUP BY IIF([Functions].[X]<[Functions].[Y],CONCAT([Functions].[X],[Functions].[Y]),CONCAT([Functions].[Y], [Functions].[X]))) AS tblxOne GROUP BY tblxOne.XY) AS tblxTwo WHERE ((tblxTwo.X+tblxTwo.Y)=26) GROUP BY tblxTwo.X, tblxTwo.Y 